import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import EditProfile from './EditProfile';
import ChangePassword from './ChangePassword';
import AdminPanel from './AdminPanel';
import axios from 'axios';

const Dashboard = () => {
  const { user, logout } = useAuth();
  const [activeTab, setActiveTab] = useState('profile');
  const [protectedMessage, setProtectedMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');

  const testProtectedRoute = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:8000/protected');
      setProtectedMessage(response.data.message);
    } catch (error) {
      setProtectedMessage('Failed to access protected route');
    }
    setLoading(false);
  };

  const handleProfileUpdateSuccess = () => {
    setSuccessMessage('Profile updated successfully!');
    setActiveTab('profile');
    setTimeout(() => setSuccessMessage(''), 3000);
  };

  const handlePasswordChangeSuccess = () => {
    setSuccessMessage('Password changed successfully!');
    setActiveTab('profile');
    setTimeout(() => setSuccessMessage(''), 3000);
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case 'profile':
        return (
          <div className="card">
            <h2>User Profile</h2>
            {successMessage && <div className="success">{successMessage}</div>}
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginBottom: '20px' }}>
              <div>
                <p><strong>Email:</strong> {user?.email}</p>
                <p><strong>Username:</strong> {user?.username}</p>
                <p><strong>Status:</strong> {user?.is_active ? 'Active' : 'Inactive'}</p>
                {user?.is_admin && <p><strong>Role:</strong> Administrator</p>}
              </div>
              <div>
                <p><strong>First Name:</strong> {user?.first_name || 'Not set'}</p>
                <p><strong>Last Name:</strong> {user?.last_name || 'Not set'}</p>
                <p><strong>Phone:</strong> {user?.phone || 'Not set'}</p>
              </div>
            </div>
            <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
              <button onClick={() => setActiveTab('edit-profile')} className="btn">
                Edit Profile
              </button>
              <button onClick={() => setActiveTab('change-password')} className="btn">
                Change Password
              </button>
              <button onClick={testProtectedRoute} className="btn btn-secondary" disabled={loading}>
                {loading ? 'Testing...' : 'Test Protected Route'}
              </button>
            </div>
            {protectedMessage && (
              <div style={{ marginTop: '15px', padding: '10px', backgroundColor: '#f8f9fa', borderRadius: '4px' }}>
                {protectedMessage}
              </div>
            )}
          </div>
        );
      case 'edit-profile':
        return (
          <EditProfile
            onCancel={() => setActiveTab('profile')}
            onSuccess={handleProfileUpdateSuccess}
          />
        );
      case 'change-password':
        return (
          <ChangePassword
            onCancel={() => setActiveTab('profile')}
            onSuccess={handlePasswordChangeSuccess}
          />
        );
      case 'admin':
        return user?.is_admin ? <AdminPanel /> : <div>Access denied</div>;
      default:
        return null;
    }
  };

  return (
    <div>
      <nav className="navbar">
        <h1>Auth System</h1>
        <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
          <span>Welcome, {user?.username}!</span>
          {user?.is_admin && (
            <span style={{ 
              backgroundColor: '#28a745', 
              color: 'white', 
              padding: '2px 8px', 
              borderRadius: '12px', 
              fontSize: '12px' 
            }}>
              ADMIN
            </span>
          )}
          <button onClick={logout}>
            Logout
          </button>
        </div>
      </nav>
      
      <div className="dashboard">
        <div className="card" style={{ marginBottom: '20px' }}>
          <div style={{ display: 'flex', gap: '10px', borderBottom: '1px solid #ddd', paddingBottom: '10px' }}>
            <button
              onClick={() => setActiveTab('profile')}
              className={`btn ${activeTab === 'profile' ? '' : 'btn-secondary'}`}
              style={{ padding: '8px 16px' }}
            >
              Profile
            </button>
            <button
              onClick={() => setActiveTab('edit-profile')}
              className={`btn ${activeTab === 'edit-profile' ? '' : 'btn-secondary'}`}
              style={{ padding: '8px 16px' }}
            >
              Edit Profile
            </button>
            <button
              onClick={() => setActiveTab('change-password')}
              className={`btn ${activeTab === 'change-password' ? '' : 'btn-secondary'}`}
              style={{ padding: '8px 16px' }}
            >
              Change Password
            </button>
            {user?.is_admin && (
              <button
                onClick={() => setActiveTab('admin')}
                className={`btn ${activeTab === 'admin' ? '' : 'btn-secondary'}`}
                style={{ padding: '8px 16px' }}
              >
                Admin Panel
              </button>
            )}
          </div>
        </div>

        {renderTabContent()}
      </div>
    </div>
  );
};

export default Dashboard;
