import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';

const Dashboard = () => {
  const { user, logout } = useAuth();
  const [protectedMessage, setProtectedMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const testProtectedRoute = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:8000/protected');
      setProtectedMessage(response.data.message);
    } catch (error) {
      setProtectedMessage('Failed to access protected route');
    }
    setLoading(false);
  };

  return (
    <div>
      <nav className="navbar">
        <h1>Auth System</h1>
        <div>
          <span>Welcome, {user?.username}!</span>
          <button onClick={logout} style={{ marginLeft: '10px' }}>
            Logout
          </button>
        </div>
      </nav>
      
      <div className="dashboard">
        <div className="card">
          <h2>User Profile</h2>
          <p><strong>Email:</strong> {user?.email}</p>
          <p><strong>Username:</strong> {user?.username}</p>
          <p><strong>Status:</strong> {user?.is_active ? 'Active' : 'Inactive'}</p>
        </div>

        <div className="card">
          <h2>Protected Route Test</h2>
          <button onClick={testProtectedRoute} className="btn" disabled={loading}>
            {loading ? 'Testing...' : 'Test Protected Route'}
          </button>
          {protectedMessage && (
            <div style={{ marginTop: '10px', padding: '10px', backgroundColor: '#f8f9fa', borderRadius: '4px' }}>
              {protectedMessage}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
